#include <iostream>
#include <string>
#include <vector>
#include <stack>
using namespace std;

class Solution {
public:
    struct node {
        char c;
        int index;
        node(char _c, int _index):c(_c), index(_index){};
    };
    int longestValidParentheses(string s) {
        // Start typing your C/C++ solution below
        // DO NOT write int main() function
        if (s.size() <= 1) return 0;
        stack<node> S;
        int maxlen = 0;
        S.push(node(')', -1));  //push en ")" as a edge soldier
        int lastlen = 0;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '(') S.push(node('(', i));	//if encounter "(", just push it.
            else {
                node tmp = S.top();					//if encounter ")" position B, at the same time, stack.top() is "(", position A,
                if (tmp.c == '(') {					//which means match, so S[A, B] should be valid parentheses. 
                    S.pop();						//so update the maxlen with B - A.
                    maxlen = max(maxlen, i-S.top().index);
                }
                else S.push(node(')', i));			// if stack top() is not "(", which means not match.
            }										//so just push back the ")", 
        }
        return maxlen;
    }
};

class Solution1 {
public:
    int longestValidParentheses(string s) {
        // Note: The Solution object is instantiated only once and is reused by each test case.
		if(s.size() <= 1)
			return 0;

		int slen = s.length();

		vector<bool* > dp;
		dp.resize(slen + 2);
		for(int i = 0; i< slen + 2; i++)
		{	dp[i] = new bool[slen + 2];
			memset(dp[i], false, (slen + 2)* sizeof(bool));
		}
		
		int resultLenght = 0;
		for(int length = 1; length <= slen; length +=2)
		{
			bool lbhasTrue = false;
			for(int x = 1; x <= slen - length; x ++)
			{
				int y = x + length;
				if(s[x - 1] == '(' && s[y - 1] == ')')
				{
					if(length == 1)
						dp[x][y] = true;
					else if(dp[x + 1][y -1])
						dp[x][y] = true;
				}

				for(int k = x + 1; k < y && dp[x][y] == false; k += 2)
				{
					if(dp[x][k] & dp[k + 1][y])
						dp[x][y] = true ;
				}
				if(dp[x][y])
					lbhasTrue = true;
			}

			if(lbhasTrue)
				resultLenght = length;
			else
				break;
		}

		return resultLenght + 1;

    }
};

